import React, { useState } from 'react';
import axios from 'axios';
import { GoogleMap, useJsApiLoader, Marker, Circle } from '@react-google-maps/api';
import './App.css';

const GOOGLE_MAPS_API_KEY = "AIzaSyC-gSjkrWo8mjx8N_NR4h6a6Bk7taseW7s"; 

const circleOptions = {
  strokeColor: "#007AFF",
  strokeOpacity: 0.8,
  strokeWeight: 2,
  fillColor: "#007AFF",
  fillOpacity: 0.15,
  clickable: false,
  draggable: false,
  editable: false,
  visible: true,
  zIndex: 1
};

// ÌÉúÍ∑∏ Î¶¨Ïä§Ìä∏ (ÌïúÍµ≠Ïñ¥)
const TAGS = [
  "ÎßõÏßë", "Ïπ¥Ìéò", "Ïà†Ïßë", "Î≤†Ïù¥Ïª§Î¶¨", "Ïù¥ÌÉàÎ¶¨Ïïà", "ÏùºÏãù", "Ï§ëÏãù", "Î©ïÏãúÏπ∏", "Ïù∏ÎèÑÏöîÎ¶¨",
  "ÌååÏù∏Îã§Ïù¥Îãù", "Í∞ÄÏÑ±ÎπÑ", "Î∑∞ÎßõÏßë", "ÏïÑÎäëÌïú", "ÌôúÍ∏∞Ï∞¨", "Î°úÎß®Ìã±", "Îã®Ï≤¥", "ÌòºÎ∞•"
];

function App() {
  const [myLocation, setMyLocation] = useState({ lat: 37.5665, lng: 126.9780 }); // Í∏∞Î≥∏ ÏúÑÏπò: ÏÑúÏö∏
  const [distance, setDistance] = useState(2.0);

  const [selectedTags, setSelectedTags] = useState([]);
  const [userText, setUserText] = useState("");

  const [stores, setStores] = useState([]); 
  const [result, setResult] = useState("");
  const [scannedCount, setScannedCount] = useState(0);
  const [analyzedCount, setAnalyzedCount] = useState(0);

  const [loading, setLoading] = useState(false);

  const { isLoaded } = useJsApiLoader({
    id: 'google-map-script',
    googleMapsApiKey: GOOGLE_MAPS_API_KEY,
    language: 'ko' // ÏßÄÎèÑ Ïñ∏Ïñ¥ ÌïúÍµ≠Ïñ¥Î°ú ÏÑ§Ï†ï
  });

  const handleRecommend = async () => {
    if (selectedTags.length === 0 && userText.trim() === "") {
        alert("ÌÇ§ÏõåÎìúÎ•º ÏÑ†ÌÉùÌïòÍ±∞ÎÇò Ï∞æÍ≥†Ïã∂ÏùÄ ÎßõÏßëÏùÑ ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî.");
        return;
    }

    setLoading(true);
    setResult("");
    setStores([]);
    setScannedCount(0);
    setAnalyzedCount(0);

    try {
      const res = await axios.post('http://localhost:8000/recommend', {
        radius_km: parseFloat(distance), 
        categories: selectedTags, 
        user_detail: userText,
        lat: myLocation.lat, 
        lng: myLocation.lng
      });
      setResult(res.data.result);
      setStores(res.data.stores || []);
      setScannedCount(res.data.scanned_count || 0);
      setAnalyzedCount(res.data.analyzed_count || 0);
    } catch (e) { 
        console.error(e);
        alert("Ï∂îÏ≤ú Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§."); 
    }
    setLoading(false);
  };

  const toggleTag = (tag) => {
    setSelectedTags(prev => prev.includes(tag) ? prev.filter(t => t !== tag) : [...prev, tag]);
  };

  return (
    <div className="App">
      {loading && (
        <div className="loading-overlay">
          <div className="spinner"></div>
        </div>
      )}

      <aside className="sidebar">
        <header className="header">
          <h1 className="title">Taste Navigator AI</h1>
          <p className="subtitle">AI ÎßõÏßë Ï∂îÏ≤ú ÏÑúÎπÑÏä§</p>
        </header>

        <div className="control-group">
          <label className="control-label">ÌÉêÏÉâ Î∞òÍ≤Ω: <span>{distance} km</span></label>
          <input 
            type="range" 
            min="0.5" max="10.0" step="0.5" 
            value={distance} 
            onChange={e => setDistance(e.target.value)} 
            className="radius-slider"
          />
        </div>

        <div className="control-group">
            <label className="control-label">ÏõêÌïòÎäî ÎßõÏßëÏùÑ ÏÑ§Î™ÖÌï¥Ï£ºÏÑ∏Ïöî</label>
            <textarea 
                value={userText}
                onChange={(e) => setUserText(e.target.value)}
                placeholder="Ïòà: Ï°∞Ïö©Ìïú Î∂ÑÏúÑÍ∏∞ÏóêÏÑú Ïª§ÌîºÍ∞Ä ÎßõÏûàÎäî Ïπ¥Ìéò"
                className="text-input"
            />
        </div>

        <div className="control-group">
          <label className="control-label">ÎòêÎäî ÌÇ§ÏõåÎìúÎ•º ÏÑ†ÌÉùÌïòÏÑ∏Ïöî</label>
          <div className="tag-list">
            {TAGS.map(tag => (
              <button 
                key={tag} 
                onClick={() => toggleTag(tag)} 
                className={`tag-btn ${selectedTags.includes(tag) ? 'selected' : ''}`}
              >
                {tag}
              </button>
            ))}
          </div>
        </div>

        <div className="results-panel">
            {result ? (
              <>
                <div className="stats-bar">
                    <div className="stat-item">ÌÉêÏÉâÎêú ÏãùÎãπ: <span>{scannedCount}</span></div>
                    <div className="stat-item">Î∂ÑÏÑùÎêú ÏãùÎãπ: <span>{analyzedCount}</span></div>
                </div>
                <pre className="results-content">{result}</pre>
              </>
            ) : (
                <div className="results-placeholder">
                  <span className="results-placeholder-icon">üçΩÔ∏è</span>
                  <p>AI Ï∂îÏ≤ú Í≤∞Í≥ºÍ∞Ä<br/>Ïó¨Í∏∞Ïóê ÌëúÏãúÎê©ÎãàÎã§.</p>
                </div>
            )}
        </div>

        <button onClick={handleRecommend} disabled={loading} className="action-button">
          {loading ? "Î∂ÑÏÑù Ï§ë..." : "ÎßõÏßë Ï∞æÍ∏∞"}
        </button>
      </aside>

      <main className="map-container">
        {isLoaded && (
          <GoogleMap 
            mapContainerStyle={{ width: '100%', height: '100%' }} 
            center={myLocation} 
            zoom={13}
            options={{ 
              styles: [ // Dark mode for map
